package com.example.connect_back.Account;import com.example.connect_back.Account.DTO.RegisterAccountDTO;import jakarta.validation.Valid;import java.util.List;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@CrossOrigin(origins = "http://localhost:5173")@RequiredArgsConstructor@RestControllerpublic class AccountController {    private final AccountService accountService;    @GetMapping("/findAll")    public List<Account> getAccounts() {        return accountService.findAllAccounts();    }    @PostMapping("/signUp")    public ResponseEntity<String> saveAccount(@Valid @RequestBody RegisterAccountDTO registerAccountDTO) {        Account account = new Account();        account.setUserId(registerAccountDTO.getUserId());        account.setUserPassword(registerAccountDTO.getUserPassword());        accountService.saveAccount(account);        return ResponseEntity.ok("Account creation complete.");    }    @PostMapping("/signIn")    public boolean authenticateAccount(@Valid @RequestBody RegisterAccountDTO registerAccountDTO) {        Account account = new Account();        account.setUserId(registerAccountDTO.getUserId());        account.setUserPassword(registerAccountDTO.getUserPassword());        return accountService.authAccount(account);    }}