package com.example.connect_back.DTO;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Size;import lombok.Data;@Datapublic class RegisterAccountDTO {    @NotBlank(message = "User ID는 필수 입력값입니다.")    @Size(min = 4, max = 20, message = "User ID는 4자 이상 20자 이하로 입력하세요.")    private String userId;    @NotBlank(message = "User Password는 필수 입력값입니다.")    @Size(min = 6, message = "User Password는 최소 6자 이상으로 입력하세요.")    private String userPassword;}