package com.example.connect_back.KoreanSentence;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@NoArgsConstructor@Entity(name = "korean_sentence")public class KorSentence {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    public KorSentence(String level, String sentence) {        this.level = level;        this.sentence = sentence;    }    @Column    private String level;    @Column    private String sentence;}