package com.example.connect_back.KoreanSentence;import com.example.connect_back.KoreanSentence.DTO.SentenceRequest;import java.util.List;import lombok.Data;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@Data@RestController@CrossOrigin(origins = "http://localhost:5173")@RequestMapping("KorSen")public class KorSentenceController {    private final KorSentenceService korSentenceService;    @GetMapping("/trial")    public void trial() {        System.out.println("TRIAL");    }    @GetMapping("/findAllKorSentence")    public List<KorSentence> getKoreanSentences() {        return korSentenceService.findAllKoreanSentence();    }    @PostMapping("/save-sentences")    public String saveSentences(@RequestBody SentenceRequest request) {        System.out.println(request.getLevel());        System.out.println(request.getSentences());        korSentenceService.saveSentences(request.getLevel(), request.getSentences());        return "SUCCESS";    }    @GetMapping("/random/{level}")    public String getRandomSentence(@PathVariable String level) {        String randomSentence = korSentenceService.getSentence(level);        if (randomSentence == null) {            return "There is no sentence of the level.";        }        return randomSentence;    }}