package com.example.connect_back.KoreanSentence;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface KorSentenceRepository extends JpaRepository<KorSentence, Long> {    @Query(value = "SELECT sentence FROM korean_sentence WHERE level = :level ORDER BY RAND() LIMIT 1", nativeQuery = true)    String getRandomSentenceByLevel(@Param("level") String level);    boolean existsBySentence(String sentence);}