package com.example.connect_back.OpenAI.DTO;import com.example.connect_back.OpenAI.Message;import java.util.ArrayList;import java.util.List;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class ChatRequest {    private String model;    private List<Message> messages;    public ChatRequest(String model) {        this.model = model;        this.messages = new ArrayList<>();    }    public ChatRequest(String model, String prompt) {        this.model = model;        this.messages = new ArrayList<>();        this.messages.add(new Message("user", prompt));    }    public void makeGeneralRequest(String sentence) {        String generalContent = sentence;        this.messages.add(new Message("user", generalContent));    }    public void makeTransRequest(String koreanSentence) {        String transContent = "\"" + koreanSentence + "\" 이 한국어 문장 영어로 번역";        this.messages.add(new Message("user", transContent));    }    public void makeReasonRequest(String koreanSentence, String engSentence) {        String reasonContent =  "\"" + koreanSentence + "\", \"" + engSentence + "\" 영어로 번역한 결과를 평가 부탁해(한국어로)";        this.messages.add(new Message("user", reasonContent));    }    public void makeCusRequest(String koreanSentence, String engSentence) {        String cusContent = "한국어 -> 영어  "            + ", 한국어: " + koreanSentence + ", 영어: " + engSentence;        this.messages.add(new Message("user", cusContent));    }}