package com.example.connect_back.OpenAI.DTO;import com.example.connect_back.OpenAI.Choice;import java.util.List;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.RequiredArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@RequiredArgsConstructorpublic class ChatResponse {    private List<Choice> choices;    public String resolveContent() {        return this.choices.get(0).getMessage().getContent();    }}